INSERT INTO `belmar_site_htmlsnippets` VALUES (1,0,0,'fiDefaultEmailTpl','The default chunk used for the email. Please do not edit this chunk, as this will be overwritten when updating FormIt.',0,3,0,'<p>[[+fields]]</p>',0,'a:0:{}',0,''),(2,0,0,'fiDefaultFiarTpl','The default chunk used for the autoresponder email. Please do not edit this chunk, as this will be overwritten when updating FormIt.',0,3,0,'<p>Hello [[+name]],</p>\n\n<p>Your message has been received. We will respond as soon as possible. Thank you for contacting us.</p>\n\n<p>NOTE: This is an automatic response; please do not respond to this message directly.</p>\n\n<p>Here is your message:<br />\n[[+message:nl2br]]</p>',0,'a:0:{}',0,''),(3,0,0,'fiDefaultOptGroupTpl','The default chunk used by the FormItCountryOptions snippet for the select optgroup. Please do not edit this chunk, as this will be overwritten when updating FormIt.',0,3,0,'<optgroup label=\"[[+text]]\">\n    [[+options]]\n</optgroup>',0,'a:0:{}',0,''),(4,0,0,'fiDefaultOptionTpl','The default chunk used by the FormItCountryOptions snippet for the select option. Please do not edit this chunk, as this will be overwritten when updating FormIt.',0,3,0,'<option value=\"[[+value]]\"[[+selected]]>[[+text]]</option>',0,'a:0:{}',0,''),(5,1,0,'tpl.AjaxForm.example','',0,5,0,'<form action=\"[[~[[*id]]]]\" method=\"post\" class=\"ajax_form af_example\">\n\n    <div class=\"form-group\">\n        <label class=\"control-label\" for=\"af_name\">[[%af_label_name]]</label>\n        <div class=\"controls\">\n            <input type=\"text\" id=\"af_name\" name=\"name\" value=\"[[+fi.name]]\" placeholder=\"\" class=\"form-control\"/>\n            <span class=\"error_name\">[[+fi.error.name]]</span>\n        </div>\n    </div>\n\n    <div class=\"form-group\">\n        <label class=\"control-label\" for=\"af_email\">[[%af_label_email]]</label>\n        <div class=\"controls\">\n            <input type=\"email\" id=\"af_email\" name=\"email\" value=\"[[+fi.emai