ay_diff(array_keys($modx->getFields(\'localizatorContent\')), array(\'id\', \'resource_id\')))){\n                $q = $modx->newQuery(\"localizatorContent\")\n                    ->where(array(\n                        \"resource_id\" => $id,\n                        \"key\" => $key,\n                    ))\n                    ->select($field);\n                if ($q->prepare() && $q->stmt->execute()){\n                    $output = $q->stmt->fetchColumn();\n                }\n            }\n            elseif (in_array($field, array_keys($modx->getFields(\'modResource\')))){\n                $output = $resource->get($field);\n            }\n            elseif ($tv = $modx->getObject(\'modTemplateVar\', array(\'name\' => $field))){\n                if ($tv->get(\'localizator_enabled\')){\n                    $q = $modx->newQuery(\"locTemplateVarResource\")\n                        ->where(array(\n                            \"contentid\" => $id,\n                            \"key\" => $key,\n                            \"tmplvarid\" => $tv->get(\'id\'),\n                        ))\n                        ->select(\'value\');\n                    if ($q->prepare() && $q->stmt->execute()){\n                        if ($output = $q->stmt->fetchColumn()){\n                            $output = localizatorContent::renderTVOutput($modx, $tv, $output, $id);\n                        }\n                    }\n                }\n                else{\n                    $output = $resource->getTVValue($field);\n                }\n            }\n            return $output;\n        });\n        break;\n}',0,NULL,1,'',0,'core/components/localizator/elements/plugins/plugin.localizator.php'),(14,1,0,'_modificators','',0,0,0,'/** @var modX $modx */\nswitch ($modx->event->name) {\n    case \'pdoToolsOnFenomInit\':\n \n        /** @var Fenom $fenom **/\n        $fenom->addModifier(\'phone\', function ($phone) {\n            $phone = str_replace(array(\'_\', \'-\', \'—\',\' \'),